mapper.select:
SELECT 

FROM auto_area a

WHERE a.id = #{id}

SELECT
FROM auto_area a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.area_name LIKE '%'||#{areaName}||'%' '%'+#{areaName}+'%' concat('%',#{areaName},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM auto_area a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE auto_area SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
