mapper.select:
SELECT

FROM auto_province a

WHERE a.pid = #{id}

SELECT
FROM auto_province a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.p_name LIKE '%'||#{pName}||'%' '%'+#{pName}+'%' concat('%',#{pName},'%')
AND a.p_code LIKE '%'||#{pCode}||'%' '%'+#{pCode}+'%' concat('%',#{pCode},'%')
AND a.p_desc LIKE '%'||#{pDesc}||'%' '%'+#{pDesc}+'%' concat('%',#{pDesc},'%')
AND a.aid = #{aid}
ORDER BY ${page.orderBy}
ORDER BY a.create_date ASC

SELECT

FROM auto_province a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE auto_province SET
del_flag = #{DEL_FLAG_DELETE}


		WHERE pid = #{id}


		WHERE aid = #{aid}
