mapper.select:
SELECT 

FROM auto_version a

WHERE a.vid = #{id}

SELECT 

FROM auto_version a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.v_name LIKE 
			'%'||#{vName}||'%'
			'%'+#{vName}+'%'
			concat('%',#{vName},'%')


		AND a.v_code LIKE 
			'%'||#{vCode}||'%'
			'%'+#{vCode}+'%'
			concat('%',#{vCode},'%')


		AND a.begin_date = #{beginDate}


		AND a.end_date = #{endDate}


		AND a.pid = #{pid}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM auto_version a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE auto_version SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE vid = #{id}


		WHERE pid = #{pid}
